# PHP & Composer
sudo apt-get update
sudo apt-get install curl -y
sudo apt-get install php php-curl php-dom php-sqlite3 zip unzip -y
sudo curl -sS https://getcomposer.org/installer -o composer-setup.php
sudo php composer-setup.php --install-dir=/usr/local/bin --filename=composer
sudo composer self-update
sudo composer -v

# Node.js
sudo curl -fsSL https://deb.nodesource.com/setup_20.x -o nodesource_setup.sh
sudo -E bash nodesource_setup.sh
sudo apt-get install -y nodejs

sudo cd /app

sudo composer create-project laravel/laravel tmp
sudo cp -R tmp/. ./ext-users-api
sudo rm -rf tmp

sudo cd /app/ext-users-api

# Criar Aplicativo Laravel
# sudo composer install --ignore-platform-req=ext-xml --ignore-platform-req=ext-dom --ignore-platform-req=ext-xmlwriter

sudo code-server --auth none --bind-addr 0.0.0.0:80 /app/ext-users-api